#!/usr/bin/env python3# -*- coding: utf-8 -*-' a test module '__author__ = 'hualai yu'import xlrdclass ExcelUtil():    def __init__(self, excelPath, sheetName):        self.data = xlrd.open_workbook(excelPath)        self.table = self.data.sheet_by_name(sheetName)        self.keys = self.table.row_values(0)        self.rowNum = self.table.nrows        self.colNum = self.table.ncols    def dict_data(self):        if self.rowNum <= 1:            print("总行数小于1")        else:            r = []            j = 1            for i in range(self.rowNum - 1):                s = {}                values = self.table.row_values(j)                for x in range(self.colNum):                    s[self.keys[x]] = values[x]                r.append(s)                j += 1            return r    def get_cell_value(self, row, col):        return self.table.cell_value(row, col)if __name__ == '__main__':    excelPath = "/Users/yuhualai/Desktop/shert.xlsx"    sheetName = "sheet"    data = ExcelUtil(excelPath, sheetName)    print(data.dict_data())    print(data.keys)    print(data.rowNum)    print(data.colNum)    print(data.get_cell_value(3, 2))